trans-unit.source:
// STRD001.cs
// compile with: /W:2
public class Program
{
    public static void Main()
    {
        goto lab1;
        {
            // The following statements cannot be reached:
            int i = 9;   // STRD001
            i++;
        }
    lab1:
        {
        }
    }
}

