trans-unit.source:
public override async Task Execute()
{
    // Load the sound
    Sound musicSound = Content.Load<Sound>("MySound");
            
    // Create a sound instance
    SoundInstance music = musicSound.CreateInstance();
            
    // Loop
    music.IsLooping = true;

    // Set the volume
    music.Volume = 0.25f;

    // Play the music
    music.Play();
}

trans-unit.target:
public override async Task Execute()
{
    // Carrega o som
    Sound musicSound = Content.Load<Sound>("MySound");
            
    // Cria uma instância do som
    SoundInstance music = musicSound.CreateInstance();
            
    // Repetição
    music.IsLooping = true;

    // Define o volume
    music.Volume = 0.25f;

    // Reproduz a música
    music.Play();
}

trans-unit.source:
public class MySoundScript : SyncScript
{
    public Sound MyMusic;

    private SoundInstance musicInstance;
    public bool PlayMusic;

    public override void Start()
    {
        musicInstance = MyMusic.CreateInstance();
    }

    public override void Update()
    {
        // If music isn't playing but should be, play the music.
        if (PlayMusic & musicInstance.PlayState != PlayState.Playing)
        {
            musicInstance.Play();
        }

        // If music is playing but shouldn't be, stop the music.
        else if (!PlayMusic)
        {
            musicInstance.Stop();
        }
    }
}

trans-unit.target:
public class MySoundScript : SyncScript
{
    public Sound MyMusic;

    private SoundInstance musicInstance;
    public bool PlayMusic;

    public override void Start()
    {
        musicInstance = MyMusic.CreateInstance();
    }

    public override void Update()
    {
        // Se a música não estiver reproduzindo, mas deveria, reproduz a música.
        if (PlayMusic & musicInstance.PlayState != PlayState.Playing)
        {
            musicInstance.Play();
        }

        // Se a música está reproduzindo, mas não deveria, pare a música.
        else if (!PlayMusic)
        {
            musicInstance.Stop();
        }
    }
}
