trans-unit.source:
using System;
using System.Text;
using System.Threading.Tasks;
using Stride.Core.Mathematics;
using Stride.Input;
using Stride.Engine;

namespace MyGame
{
	public class BasicAsyncScript : AsyncScript
	{	
		public override async Task Execute()
		{
			while(Game.IsRunning)
			{
				// Do some stuff every frame
				await Script.NextFrame();
			}
		}
	}
}

trans-unit.target:
usando o sistema;
usando o Sistema. Texto;
usando o Sistema. Threading. Tarefas;
usando Stride. Core.Matemática;
usando Stride. Entrada;
usando Stride. Motor;

namespace MyGame
(
	classe pública BasicAsyncScript : AsyncScript
	(	
		override público async Task Execute()
		(
			enquanto (Game.IsRunning)
			(
				\/\/ Faça algumas coisas cada quadro
				await Script.NextFrame();
			}
		}
	}
}

trans-unit.source:
	using System;
	using System.Text;
	using System.Threading.Tasks;
	using Stride.Core.Mathematics;
	using Stride.Input;
	using Stride.Engine;

	namespace MyGame
	{
		public class SampleSyncScript : SyncScript
		{			
			public override void Update()
			{
				if (Game.IsRunning)
				{
					// Do something every frame
				}
			}
		}
	}

trans-unit.target:
	usando o sistema;
	usando o Sistema. Texto;
	usando o Sistema. Threading. Tarefas;
	usando Stride. Core.Matemática;
	usando Stride. Entrada;
	usando Stride. Motor;

	namespace MyGame
	(
		classe pública SampleSyncScript : Sincronização
		(			
			atualização()
			(
				se (Game.IsRunning)
				(
					\/\/ Faça algo cada quadro
				}
			}
		}
	}
